mapper.select:
SELECT o.id orgId,COUNT(*) count FROM dhlk_basic_user u LEFT JOIN dhlk_basic_org o ON FIND_IN_SET(o.id,u.org_id) AND o.status = 0 AND u.status = 0 AND u.is_admin = 1
AND o.tenant_id = #{tenantId}
GROUP BY orgId

select count(*) from dhlk_basic_user
where login_name = #{loginName}

    AND id!=#{id}

mapper.update:
update dhlk_basic_user
name = #{name}, login_name = #{loginName}, password = #{password}, status = #{status}, org_id = #{orgId}, is_admin = #{isAdmin}, phone = #{phone}, email = #{email}, tenant_id = #{tenantId},
where id = #{id}

mapper.select:
SELECT status FROM dhlk_basic_tenant WHERE  id in (
SELECT tenant_id FROM dhlk_basic_user WHERE id = #{userId}
)
