mapper.select:
select
id, delivery_project, remark, delivery_product, create_time, status, project_id,company_id
from delivery_apply_for


        and id = #{id}


        and delivery_project = #{deliveryProject}


        and remark = #{remark}


        and delivery_product = #{deliveryProduct}


        and create_time = #{createTime}


        and status = #{status}


        and project_id = #{projectId}


        and company_id = #{companyId}
