mapper.select:
select
id, order_id, submit_time, amount, total_money, invoice_info_id
from dhlk_subcontract_plat.invoice


        and id = #{id}


        and order_id = #{orderId}


        and submit_time = #{submitTime}


        and amount = #{amount}


        and total_money = #{totalMoney}


        and invoice_info_id = #{invoiceInfoId}
