mapper.select:
select
id, company_name, company_address, company_phone, bank_account, open_bank, tax_no, consignee, phone,
consignee_address, user_id
from dhlk_subcontract_plat.invoice_info


        and id = #{id}


        and company_name = #{companyName}


        and company_address = #{companyAddress}


        and company_phone = #{companyPhone}


        and bank_account = #{bankAccount}


        and open_bank = #{openBank}


        and tax_no = #{taxNo}


        and consignee = #{consignee}


        and phone = #{phone}


        and consignee_address = #{consigneeAddress}


        and user_id = #{userId}
