mapper.select:
select
id, code, name, url, status, parent_id, perms, type
from dhlk_subcontract_plat.menu


        and id = #{id}


        and code = #{code}


        and name = #{name}


        and url = #{url}


        and status = #{status}


        and parent_id = #{parentId}


        and perms = #{perms}


        and type = #{type}
