mapper.select:
select
id, type, reason, approval_suggest, apply_time, approval_time, project_id, audit_result,company_id,project_name,company_name,company_type
from project_close


        and id = #{id}


        and type = #{type}


        and reason = #{reason}


        and approval_suggest = #{approvalSuggest}


        and apply_time = #{applyTime}


        and approval_time = #{approvalTime}


        and project_id = #{projectId}


        and audit_result = #{auditResult}


        and company_id = #{companyId}


        and project_name = #{projectName}


        and company_name = #{companyName}


        and company_type = #{companyType}
