mapper.select:
select
id, email_address, email_password, smtp_address, content, create_time
from dhlk_subcontract_plat.server_email


        and id = #{id}


        and email_address = #{emailAddress}


        and email_password = #{emailPassword}


        and smtp_address = #{smtpAddress}


        and content = #{content}


        and create_time = #{createTime}
