mapper.select:
select
id, login_name, password, company_id, company_email,status
from dhlk_subcontract_plat.subpackage_user


        and id = #{id}


        and login_name = #{loginName}


        and password = #{password}


        and company_id = #{companyId}


        and company_email = #{companyEmail}

select count(0) from subpackage_user
where login_name = #{loginName}

    and id != #{id}

select count(0) from subpackage_user
where company_email = #{companyEmail}

    and id != #{id}

select su.*,c.* from subpackage_user su
left join company c on c.id = su.company_id
where company_email = #{companyEmail}

select su.*,c.* from subpackage_user su
left join company c on c.id = su.company_id
where 1=1

    and company_name = #{companyName}


    and audit_status = #{auditStatus}


    and is_blacklist = #{isBlacklist}
